import timeimport sqlite3from flask import Flask, render_templatedef main():    sqlite_file = 'my_first_db.sqlite'    # name of the sqlite database file    #table_name1 = 'table1'  # name of the table to be created    #table_name2 = 'my_table_2'  # name of the table to be created    #new_field = 'fielder' # name of the column    #field_type = 'TEXT'  # column data type    conn = sqlite3.connect(sqlite_file)    c = conn.cursor()    conn.commit()    #c.execute('CREATE TABLE {tn} ({nf} {ft})'\            #.format(tn=table_name1, nf=new_field, ft=field_type))    #c.execute('CREATE TABLE {tn} ({nf} {ft} PRIMARY KEY)'\            #.format(tn=table_name2, nf=new_field, ft=field_type))    #c.execute('INSERT INTO table1 (fielder) VALUES ("hi there")' )        #conn.commit()	    #c.execute('SELECT * FROM table1')    while True:        user_input = input("execute SQL script:  ")                if user_input[0:6].lower() == 'select':            c.execute(user_input)            print(c.fetchall())        elif user_input[0].lower() == 'q':            return        else:            c.execute(user_input)            conn.commit()    # Committing changes and closing the connection to the database file    conn.close()
if __name__ == "__main__":
    main()# @app.route('/') #remember this because this what comes after local host, which 127.0.0.1:whatever port(5000 default), then anything after / is another page# @app.route('/home')# def printme():     # return render_template ('home.html')#if __name__ == "__main__":    #with sqlite3.connect('inventory_schema') as  
